<?php // Do not delete these lines 	if ('comments.php' == basename($_SERVER['SCRIPT_FILENAME'])) 		die ('Please do not load this page directly. Thanks!'); 	if (!empty($post->post_password)) {  		if ($_COOKIE['wp-postpass_' . COOKIEHASH] != $post->post_password) { 			?><p class="nocomments">   				<?php _e("This post is password protected. Enter the password to view comments."); ?> 			<p>   			<?php return; 		} 	} ?> <div class="comments">   <!--comments area-->   <?php if ($comments) : ?>  <h3>Comments</h3> <ol> 	<?php $i = 1; ?> 	<?php foreach ($comments as $comment) :  		$alt = ($alt == "even")?"odd":"even";?>   		<li class="comment <?php echo $alt;?>" id="comment-<?php comment_ID();?>">   			<div class="commentid">   				<span class="gravatar"><?php echo get_avatar( $comment, 40 ); ?></span>   				<a href="#comment-<?php comment_ID();?>" class="commentno"><?php echo $i;?></a>   			</div>   			<div class="body">   				<p class="commentmeta comment-author">   					<cite class="fn who"><?php comment_author_link();?></cite>   					<span class="time" title=""><?php comment_date();?></span>   				</p>   				<?php comment_text();?>   			</div>   		</li>   		<?php ++$i;?>  	<?php endforeach;  ?>  	   </ol> </div> <?php endif;?>  <?php if(comments_open()):?>		<?php if ( get_option('comment_registration') && !$user_ID ) { ?>				<p class="indent"><?php printf(__('You must be <a href="%s">logged in</a> to post a comment.'), get_option('siteurl')."/wp-login.php?redirect_to=".urlencode(get_permalink()));?></p>			<?php } else { ?>			<div id="comment_form">					<h3>Leave a reply</h3>				<form action="<?php echo get_option('siteurl'); ?>/wp-comments-post.php" method="post" id="commentform">								                <label for="comment">Message:</label>					<textarea name="comment" id="message" cols="" rows="" tabindex="1"></textarea>					<?php if ( $user_ID ) : ?>						<p><?php printf(__('Logged in as %s.'), '<a href="'.get_option('siteurl').'/wp-admin/profile.php">'.$user_identity.'</a>'); ?> <a href="<?php echo get_option('siteurl'); ?>/wp-login.php?action=logout" title="<?php _e('Log out of this account') ?>"><?php _e('Log out &raquo;'); ?></a></p>					<?php else : ?>										<label for="author">*Name:</label>						<input class="text" type="text" name="author" id="author" tabindex="2" />																<label for="email">*Email:</label>						<input class="text" type="text" name="email" id="email" tabindex="3" />																<label for="url">Website:</label>						<input class="text" type="text" name="url" id="url" tabindex="4" />									<?php endif;?>								<input name="submit" type="submit" id="submit" class="submit" tabindex="5" value="Submit" />					<input type="hidden" name="comment_post_ID" value="<?php echo $id; ?>" />			</form>		</div>		<?php } ?>		<?php else: ?>			<p class="indent">Comments are closed at this time.</p>		<?php endif ?>          